import React, { useState } from "react";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import Container from "react-bootstrap/Container";
import { Link, useNavigate } from "react-router-dom";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";

interface FormState {
  firstname: string;
  lastname: string;
  email: string;
  username: string;
  password: string;
  confirmPassword: string;
  submitted: boolean;
}

const Register: React.FC = () => {
  const [formState, setFormState] = useState<FormState>({
    firstname: "",
    lastname: "",
    email: "",
    username: "",
    password: "",
    confirmPassword: "",
    submitted: false,
  });

  const navigate = useNavigate();

  const handleInputChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = event.target;
    setFormState((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  const handleSubmit = async (event: React.FormEvent<HTMLFormElement>) => {
    event.preventDefault();
    setFormState((prevState) => ({
      ...prevState,
      submitted: true,
    }));

    // Perform form validation
    if (formState.firstname.trim() === "") {
      return;
    }
    if (formState.lastname.trim() === "") {
      return;
    }
    if (formState.username.trim().length < 4) {
      return;
    }

    if (!validateEmail(formState.email)) {
      return;
    }

    if (formState.password.trim() === "") {
      return;
    }

    if (formState.confirmPassword.trim() === "") {
      return;
    }

    if (formState.password !== formState.confirmPassword) {
      return;
    }

    // Form is valid, submit it
    console.log("Form is valid, submit it");

    try {
      const response = await createUser(formState);
      if (response.success) {
        // Redirect to the login page if createUser is successful
        navigate("/login");
      } else {
        // Handle the failure case
        console.error("User creation failed:", response.error);
      }
    } catch (error) {
      console.error("Error occurred during user creation:", error);
    }
  };

  const createUser = async (userData: FormState) => {
    try {
      const response = await fetch(
        "http://localhost:8080/api/v1/loginRegister/registerUser",
        {
          method: "POST",
          mode: "cors",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify(userData),
        }
      );
      if (response.ok) {
        const data = await response.json();
        return { success: true, data };
      } else {
        const error = await response.text();
        return { success: false, error };
      }
    } catch (error) {
      throw new Error("error.message");
    }
  };

  const validateEmail = (email: string): boolean => {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return emailRegex.test(email);
  };

  const validateStrongPassword = (password: string): boolean => {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return emailRegex.test(email);
  };

  return (
    <Container
      className="d-flex justify-content-center align-items-center vh-100"
      style={{ fontSize: "14px" }}
    >
      <div className="border p-4">
        <Form onSubmit={handleSubmit}>
          <Form.Group controlId="firstname">
            <Form.Label>Firstname</Form.Label>
            <Form.Control
              type="text"
              name="firstname"
              placeholder="Enter firstname"
              value={formState.firstname}
              onChange={handleInputChange}
              className={
                formState.submitted && formState.firstname.trim() === ""
                  ? "is-invalid"
                  : ""
              }
            />
            {formState.submitted && formState.firstname.trim() === "" && (
              <Form.Control.Feedback type="invalid">
                Please enter a firstname.
              </Form.Control.Feedback>
            )}
          </Form.Group>

          <Form.Group controlId="lastname">
            <Form.Label>Lastname</Form.Label>
            <Form.Control
              type="text"
              name="lastname"
              placeholder="Enter lastname"
              value={formState.lastname}
              onChange={handleInputChange}
              className={
                formState.submitted && formState.lastname.trim() === ""
                  ? "is-invalid"
                  : ""
              }
            />
            {formState.submitted && formState.lastname.trim() === "" && (
              <Form.Control.Feedback type="invalid">
                Please enter a lastname.
              </Form.Control.Feedback>
            )}
          </Form.Group>

          <Form.Group controlId="username">
            <Form.Label>Username</Form.Label>
            <Form.Control
              type="text"
              name="username"
              placeholder="Enter username"
              value={formState.username}
              onChange={handleInputChange}
              className={
                formState.submitted && formState.username.trim().length < 4
                  ? "is-invalid"
                  : ""
              }
            />
            {formState.submitted && formState.username.trim().length < 4 && (
              <Form.Control.Feedback type="invalid">
                Please enter a long enough username.
              </Form.Control.Feedback>
            )}
          </Form.Group>

          <Form.Group controlId="email">
            <Form.Label>Email</Form.Label>
            <Form.Control
              type="text"
              name="email"
              placeholder="Enter email"
              value={formState.email}
              onChange={handleInputChange}
              className={
                formState.submitted && !validateEmail(formState.email)
                  ? "is-invalid"
                  : ""
              }
            />
            {formState.submitted && !validateEmail(formState.email) && (
              <Form.Control.Feedback type="invalid">
                Please enter an email.
              </Form.Control.Feedback>
            )}
          </Form.Group>

          <Form.Group controlId="password">
            <Form.Label>Password</Form.Label>
            <Form.Control
              type="password"
              name="password"
              placeholder="Enter password"
              value={formState.password}
              onChange={handleInputChange}
              className={
                formState.submitted && formState.password.trim() === ""
                  ? "is-invalid"
                  : ""
              }
            />
            {formState.submitted && formState.password.trim() === "" && (
              <Form.Control.Feedback type="invalid">
                Please enter a password.
              </Form.Control.Feedback>
            )}
          </Form.Group>

          <Form.Group controlId="confirmPassword">
            <Form.Label>Confirm Password</Form.Label>
            <Form.Control
              type="password"
              name="confirmPassword"
              placeholder="Confirm password"
              value={formState.confirmPassword}
              onChange={handleInputChange}
              className={
                formState.submitted &&
                formState.password !== formState.confirmPassword
                  ? "is-invalid"
                  : ""
              }
            />
            {formState.submitted &&
              formState.password !== formState.confirmPassword && (
                <Form.Control.Feedback type="invalid">
                  Passwords do not match.
                </Form.Control.Feedback>
              )}
          </Form.Group>

          <Row className="justify-content-between">
            <Col>
              <Button type="submit">Submit</Button>
            </Col>
            <Col xs="auto">
              <Link to="/">
                <Button variant="secondary">Cancel</Button>
              </Link>
            </Col>
          </Row>
        </Form>
      </div>
    </Container>
  );
};

export default Register;
